--PATRONES DE CONSUMO ESTACIONAL
--(Informacion localizada: INSIGHT VENTAS)
SELECT 
    DATENAME(MONTH, fecha_orden_tomada) AS mes_nombre,
    MONTH(fecha_orden_tomada) AS mes_numero,
    COUNT(*) AS cantidad_pedidos,
    SUM(total_compra) AS total_ventas,
    AVG(total_compra) AS venta_promedio
FROM 
    Ordenes
WHERE 
    fecha_orden_tomada IS NOT NULL
GROUP BY 
    DATENAME(MONTH, fecha_orden_tomada), 
    MONTH(fecha_orden_tomada)
ORDER BY 
    mes_numero DESC;

