--DETERMINACION DE UTILIDAD BRUTA
--(Informacion localizada: INSIGHT VENTAS)
SELECT 
    SUM (do.cantidad * do.precio) AS TOTAL_INGRESOS,
    SUM (do.cantidad * p.precio) AS TOTAL_COSTOS,
    SUM ( (do.precio - p.precio) * do.cantidad) AS UTILIDAD_BRUTA
FROM
    DetalleOrdenes do
JOIN 
    Productos p ON do.id_productos = p.id_productos;

--DETERMINACION DE GANANCIA FLASH
--(Informacion localizada: INSIGHT VENTAS)
SELECT 
    (SELECT 
        SUM (do.cantidad * do.precio) 
        FROM
        DetalleOrdenes do) AS TOTAL_INGRESOS,

    (SELECT 
        SUM (do.cantidad * p.precio) 
        FROM
        DetalleOrdenes do
        JOIN 
        Productos p ON do.id_productos = p.id_productos)  AS TOTAL_COSTOS,
     
     (SELECT
        SUM ( (do.precio - p.precio) * do.cantidad) 
        FROM
        DetalleOrdenes do
        JOIN 
        Productos p ON do.id_productos = p.id_productos)  AS UTILIDAD_BRUTA,

     (SELECT SUM(total_pago)
     FROM Nomina) AS TOTAL_COSTO_MANO_OBRA;

--DETERMINACION MARGEN UTILIDAD BRUTA
--(Informacion localizada: INSIGHT VENTAS)
SELECT 
    ROUND (
        (
            (SUM(do.cantidad * do.precio) - sum(do.cantidad * p.precio))
            / NULLIF (SUM(do.cantidad * do.precio),0)
        ) *100,
        2
    ) AS MARGEN_UTILIDAD_BRUTA
FROM
    DetalleOrdenes do
JOIN Productos p ON do.id_productos = p.id_productos;

